import React, { useState, useEffect } from 'react';
import NewTransactions from './NewTransactions';
import TransactionList from './TransactionList';


function Home() {
  const [allTransactions, setAllTransactions] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');

  useEffect(() => {

    const queryParam = searchQuery ? `?description_like=${searchQuery}` : ""; // Query param for search
    const fullURL = `http://localhost:3000/transactions${queryParam}`;

    fetch(fullURL)
      .then(response => response.json())
      .then(data => {
        console.log(data);
        setAllTransactions(data);
      })
      .catch(error => console.error(error));
  }, [searchQuery]);

  function handleSearch(e) {
    setSearchQuery(e.target.value);
  }

  function handleDeleteTransaction(transactionId) {
       fetch(`http://localhost:3000/transactions${transactionId}`, {
        method: 'DELETE',
      })
      .then((r)=>r.json())
      .then(()=>
        setAllTransactions(prevTransactions =>
        prevTransactions.filter(transaction => transaction.id !== transactionId))
      );
    } 



  return (
    <div>
      <h1 className="welcome-header">Welcome to The Bank of Flatiron</h1>
      <div>
        <input
          id="input"
          type="text"
          placeholder="Search Here..."
          onChange={handleSearch}
        />
        <button id="searchButton">Search</button>
      </div>
      <TransactionList
        transactions={allTransactions}
        onDeleteTransaction={handleDeleteTransaction}
      />
      <NewTransactions />
    </div>
  );
}

export default Home;
